package util

import (
	"github.com/caarlos0/env"
	"go.uber.org/zap"
)

type ModuleConfigVariables struct {
	Name                    string   `env:"MODULE_NAME" envDefault:"cicd"`
	BaseMinVersionSupported string   `env:"MODULE_BASE_MIN_VERSION_SUPPORTED" envDefault:"v0.0.1"`
	Assets                  []string `env:"MODULE_ASSETS" envDefault:"" envSeparator:","`
	Description             string   `env:"MODULE_DESCRIPTION" envDefault:"<div class=\"module-details__feature-info fs-14 fw-4\"><p>This CI/CD integration enables software development teams to automate the build and deployment process so that they can focus on meeting the business requirements, maintaining code quality, and ensuring security.</p><h3 class=\"module-details__features-list-heading fs-14 fw-6\">Features:</h3><ul class=\"module-details__features-list pl-22 mb-24\"><li>Leverages Kubernetes auto-scaling and centralized caching to give you unlimited cost-efficient CI workers.</li><li>Supports pre-CI and post-CI integrations for code quality monitoring.</li><li>Seamlessly integrates with Clair for image vulnerability scanning.</li><li>Supports different deployment strategies: Blue/Green, Rolling, Canary, and Recreate.</li><li>Check logs, events, and manifests or exec inside containers for debugging.</li><li>Provides deployment metrics like; deployment frequency, lead time, change failure rate, and mean-time recovery.</li><li>Seamless integration with Grafana for continuous application metrics like CPU and memory usage, status code, throughput, and latency on the dashboard.</li></ul></div>"`
	Title                   string   `env:"MODULE_TITLE" envDefault:"Build and Deploy (CI/CD)"`
	Icon                    string   `env:"MODULE_ICON" envDefault:"data:image/png;base64,"`
	Info                    string   `env:"MODULE_INFO" envDefault:"Enables continuous code integration and deployment."`
	Integrations            string   `env:"INTEGRATIONS"`
}

type ModuleConfig struct {
	ModuleConfig *ModuleConfigVariables
}

func NewModuleConfig(logger *zap.SugaredLogger) (*ModuleConfig, error) {
	cfg := &ModuleConfigVariables{}
	err := env.Parse(cfg)
	if err != nil {
		logger.Errorw("error on parsing module config", "err", err)
		return &ModuleConfig{}, err
	}
	moduleConfig := &ModuleConfig{
		ModuleConfig: cfg,
	}
	return moduleConfig, nil
}
